import React, { useEffect, useState } from 'react';
import axios from 'axios';

const App = () => {
  return (
    <div>
      <Card />
    </div>
  );
};

const Card = () => {
  const imageData = [
    {
      imageUrl:
        'data:image/jpeg;base64,',
      text: 'hello there',
    },
    {
      imageUrl:
        'data:image/jpeg;base64,',
      text: 'welcome to my app',
    },
    {
      imageUrl:
        'data:image/jpeg;base64,',
      text: 'still on development',
    },
  ];

  const [currentImage, setCurrentImage] = useState(null);
  const [count, setCount] = useState(0);
  const [bestCount, setBestCount] = useState(0);

  useEffect(() => {
    const getRandomImage = () => {
      const randomIndex = Math.floor(Math.random() * imageData.length);
      setCurrentImage(imageData[randomIndex]);
    };

    getRandomImage();
  }, []);

  const handleClick = () => {
    setCount(count + 1);
    setBestCount(Math.max(bestCount, count));
  };

  return (
    <div className="main-card">
      <div className="image">
        {currentImage ? (
          <img src={currentImage.imageUrl} alt="random" className="profile" />
        ) : (
          <p>Loading...</p>
        )}
      </div>
      <div>
        <h3>{currentImage?.text || 'No image loaded'}</h3>
      </div>

      <div className="count">
        <p>
          count: <span>{count}</span>
        </p>
        <br />
        <span>best count: {bestCount}</span>
        <br />
        <button className="btn-1" onClick={handleClick}>
          click
        </button>
      </div>
    </div>
  );
};

